import fetch from "isomorphic-unfetch";
import { NextUrqlAppContext, withUrqlClient } from "next-urql";
import NextApp, { AppProps } from "next/app";
import React from "react";

const App = ({ Component, pageProps }: AppProps) => {
  return <Component {...pageProps} />;
};

App.getInitialProps = async (ctx: NextUrqlAppContext) => {
  const appProps = await NextApp.getInitialProps(ctx);

  return {
    ...appProps,
  };
};

export default withUrqlClient((_ssr: object, ctx: NextUrqlPageContext) => ({
  url: "https://learn-anything-db.herokuapp.com/v1/graphql",
  fetchOptions: {
    headers: {
      Authorization: `Bearer ${ctx?.req?.headers?.authorization ?? ""}`,
    },
  },
  fetch,
}))(
  // @ts-ignore
  App
);
